const button = () => {



filtra("tutto")



function filtra(elemento) {
    var x, i;
    x = document.getElementsByClassName("filterDiv");
    if (elemento == "tutto") elemento = "";
    for (i = 0; i < x.length; i++) {
        rimuoviPost(x[i], "show");
        if (x[i].className.indexOf(elemento) > -1) aggiungiPost(x[i], "show");
    }
}

//Mi mostra gli elementi filtrati
function aggiungiPost(element, name) {
    var i, arr1, arr2;
    arr1 = element.className.split(" ");
    arr2 = name.split(" ");
    for (i = 0; i < arr2.length; i++) {
        if (arr1.indexOf(arr2[i]) === -1) {
            element.className += " " + arr2[i];
        }
    }
}

//Mi nasconde gli elementi che non voglio che siano mostrati quando schiaccio il bottone
function rimuoviPost(element, name) {
    var i, arr1, arr2;
    arr1 = element.className.split(" ");
    arr2 = name.split(" ");
    for (i = 0; i < arr2.length; i++) {
        while (arr1.indexOf(arr2[i]) > -1) {
            arr1.splice(arr1.indexOf(arr2[i]), 1);
        }
    }
    element.className = arr1.join(" ");
}

//Bottone "Active"
var btnContainer = document.getElementById("myBtnContainer");
var btns = btnContainer.getElementsByClassName("btn1");
for (var i = 0; i < btns.length; i++) {
    btns[i].addEventListener("click", function() {
        var current = document.getElementsByClassName("active");
        current[0].className = current[0].className.replace(" active", "");
        this.className += " active";
    });
}

}

export default button;
