/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect }  from 'react';
import bape from '../img/bape.jpg';
import { debounce } from '../utilities/helpers';  



const Navbar = () => {
  const [prevScrollPos, setPrevScrollPos] = useState(0); 
  const [visible, setVisible] = useState(true);  

  const handleScroll = debounce(() => {
    const currentScrollPos = window.pageYOffset;

    setVisible((prevScrollPos > currentScrollPos && prevScrollPos - currentScrollPos > 70) || currentScrollPos < 10);

    setPrevScrollPos(currentScrollPos);
  }, 100);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => window.removeEventListener('scroll', handleScroll);

  }, [prevScrollPos, visible, handleScroll]);

  const navbarStyles = {
    position: 'fixed',
    height: '60px',
    width: '100%',
    backgroundColor: 'grey',
    textAlign: 'center',
    transition: 'top 0.6s' 
  }

  return (
  
    <div id="navbar" style={{ ...navbarStyles, top: visible ? '0' : '-60px' }} >
        <nav class=" navbar navbar-expand-lg navbar-dark " style="background-color: black;">
            <a class="navbar-brand"><img src={"bape.jpg"} alt="Alex" title="Alex" width="50" height="50"/></a>

            <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span class="navbar-toggler-icon"></span>
             </button>

            <div class="collapse navbar-collapse" id="navbarSupportedContent">
                <ul class="navbar-nav ml-auto">


                    <li class="nav-item">
                        <a class="nav-link" href="aboutme.html"><b>Chi Sono</b></a>
                        <a class="nav-link" href="contact.html"><b>Contatti</b></a>
                    </li>
                </ul>


            </div>
        </nav>
    </div>

  );
};

export default Navbar;
