import React,{useState} from 'react';
import Navbar from './components/navbar';
import './App.css';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from './components/button';
import Post from './components/post.js';
import Nero from './img/Nero.jpg';


const posts= [
  {id:0, immagine: Nero, titolo:'TECNO', descrizione:'descrizione1', aggiornamento:'aggiornamento1', sezione:'tecnologia'},
  {id:1, immagine: Nero, titolo:'MUSIC', descrizione:'descrizione2', aggiornamento:'aggiornamento2', sezione:'musica'},
  {id:2, immagine: Nero, titolo:'VIDEOGIOCHI', descrizione:'descrizione3', aggiornamento:'aggiornamento3', sezione:'videogiochi'},
  {id:2, immagine: Nero, titolo:'FILM', descrizione:'descrizione4', aggiornamento:'aggiornamento4', sezione:'film'},
  {id:2, immagine: Nero, titolo:'CONSIGLI', descrizione:'descrizione4', aggiornamento:'aggiornamento4', sezione:'consigli'},

];




function App(){

  const [state,setState]= useState(posts);


  const filtra=(e)=>{
    let word= e.target.value;

    if(word === 'All'){
      setState(posts);
    }
    else if(word==='tecnologia'){
      const filtered= posts.filter(post=>post.sezione==='tecnologia');
      setState(filtered);
    }
    else if(word==='musica'){
      const filtered= posts.filter(post=>post.sezione==='musica');
      setState(filtered);
    }
    else if(word==='videogiochi'){
      const filtered= posts.filter(post=>post.sezione==='videogiochi');
      setState(filtered);
    }
    else if(word==='film'){
      const filtered= posts.filter(post=>post.sezione==='film');
      setState(filtered);
    }
    else if(word==='consigli'){
      const filtered= posts.filter(post=>post.sezione==='consigli');
      setState(filtered);
    }
  }


    return (
      <Router>
      <div className="App">
       <Navbar/>
     <Button/>
       <hr/>
      {state.map(post =>(
        <Post key={post.id} post={post} sezione={post.sezione}/>
      ))}
      </div>
      </Router>
    );
  }

export default App;
