import React,{useState} from 'react';
import Navbar from './components/navbar';
import './App.css';
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from './components/button';
import Post from './components/post.js';
import Nero from './img/Nero.jpg';


const posts= [
  {id:0, immagine: Nero, titolo:'titolo1', descrizione:'descrizione1', aggiornamento:'aggiornamento1', sezione:'videogiochi'},
  {id:1, immagine: Nero, titolo:'titolo2', descrizione:'descrizione2', aggiornamento:'aggiornamento2', sezione:'videogiochi'},
  {id:2, immagine: Nero, titolo:'titolo3', descrizione:'descrizione3', aggiornamento:'aggiornamento3', sezione:'videogiochi'},
  {id:2, immagine: Nero, titolo:'titolo4', descrizione:'descrizione4', aggiornamento:'aggiornamento4', sezione:'tecnologia'},

];




function App(){

  const [state,setState]= useState(posts);


  const filtra=(e)=>{
    let word= e.target.value;

    if(word === 'All'){
      setState(posts);
    }
    else if(word==='videogiochi'){
      const filtered= posts.filter(post=>post.sezione==='videogiochi');
      setState(filtered);
    }
  }


    return (
      <Router>
      <div className="App">
       <Navbar/>
       <section className="container-fluid">
        <div className="position-body responsive">
            <div id="myBtnContainer">
                <button className="btn1 big-button" value='All' onClick={filtra}> Mostra Tutto</button>
                <button className="btn1 big-button" value='tecnologia' onClick={filtra}> Tecnologia</button>
                <button className="btn1 big-button" value='All' onClick={filtra}>Musica</button>
                <button className="btn1 big-button" value='videogiochi'onClick={filtra}> Videogiochi</button>
                <button className="btn1 big-button" value='All'onClick={filtra}>Film-SerieTv</button>
                <button className="btn1 big-button" value='All'onClick={filtra}>Consigli</button>
            </div>
        </div>
    </section>
       <hr/>
      {state.map(post =>(
        <Post key={post.id} post={post} sezione={post.sezione}/>
      ))}
      </div>
      </Router>
    );
  }

export default App;
